input.content:
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    min-height: 100vh;
    display: flex;
    justify-content: center;
    align-items: center;
    color: #333;
}

.container {
    background: white;
    border-radius: 20px;
    padding: 50px 60px;
    box-shadow: 0 20px 60px rgba(0, 0, 0, 0.3);
    text-align: center;
    min-width: 400px;
}

h1 {
    color: #667eea;
    margin-bottom: 40px;
    font-size: 2.5rem;
}

.timer-display {
    margin-bottom: 30px;
}

.state-label {
    font-size: 1.5rem;
    font-weight: 600;
    margin-bottom: 20px;
    text-transform: uppercase;
    letter-spacing: 2px;
}

.state-label.work {
    color: #e74c3c;
}

.state-label.break {
    color: #2ecc71;
}

.countdown {
    font-size: 5rem;
    font-weight: bold;
    color: #2c3e50;
    font-variant-numeric: tabular-nums;
    margin: 20px 0;
}

.session-counter {
    background: #f8f9fa;
    padding: 15px;
    border-radius: 10px;
    margin-bottom: 30px;
}

.session-counter p {
    font-size: 1.2rem;
    color: #555;
}

#sessionCount {
    color: #667eea;
    font-weight: bold;
    font-size: 1.5rem;
}

.controls {
    display: flex;
    gap: 15px;
    justify-content: center;
}

.btn {
    padding: 15px 30px;
    font-size: 1rem;
    font-weight: 600;
    border: none;
    border-radius: 10px;
    cursor: pointer;
    transition: all 0.3s ease;
    text-transform: uppercase;
    letter-spacing: 1px;
}

.btn:hover:not(:disabled) {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.2);
}

.btn:active:not(:disabled) {
    transform: translateY(0);
}

.btn:disabled {
    opacity: 0.5;
    cursor: not-allowed;
}

.btn-primary {
    background: #667eea;
    color: white;
}

.btn-primary:hover:not(:disabled) {
    background: #5568d3;
}

.btn-secondary {
    background: #95a5a6;
    color: white;
}

.btn-secondary:hover:not(:disabled) {
    background: #7f8c8d;
}

.notification {
    margin-top: 20px;
    padding: 15px;
    border-radius: 10px;
    font-size: 1.1rem;
    font-weight: 600;
    opacity: 0;
    transition: opacity 0.3s ease;
}

.notification.show {
    opacity: 1;
}

.notification.work-complete {
    background: #d4edda;
    color: #155724;
    border: 2px solid #c3e6cb;
}

.notification.break-complete {
    background: #fff3cd;
    color: #856404;
    border: 2px solid #ffeaa7;
}

@keyframes pulse {
    0%, 100% {
        transform: scale(1);
    }
    50% {
        transform: scale(1.05);
    }
}

.countdown.alert {
    animation: pulse 1s ease-in-out infinite;
    color: #e74c3c;
}

