    display: flex;
    justify-content: center;
    align-items: center;
    color: #333;
}

.container {
    background: white;
    border-radius: 20px;
    padding: 50px 60px;
    box-shadow: 0 20px 60px rgba(0, 0, 0, 0.3);
    text-align: center;
    min-width: 400px;
    transition: box-shadow 0.6s ease, border 0.6s ease;
    border: 3px solid transparent;
}

.container.work-mode {
    border-color: rgba(231, 76, 60, 0.2);
    box-shadow: 0 20px 60px rgba(231, 76, 60, 0.15);
}

.container.break-mode {
    border-color: rgba(46, 204, 113, 0.2);
    box-shadow: 0 20px 60px rgba(46, 204, 113, 0.15);
}

h1 {
    color: #667eea;
    margin-bottom: 40px;
    font-size: 2.5rem;
}

