    margin-bottom: 30px;
    transition: transform 0.4s ease;
}

.state-label {
    font-size: 1.5rem;
    font-weight: 600;
    margin-bottom: 20px;
    text-transform: uppercase;
    letter-spacing: 2px;
    transition: color 0.6s ease, transform 0.4s ease;
}

.state-label.work {
    color: #e74c3c;
}

.state-label.break {
    color: #2ecc71;
}

.countdown {
    font-size: 5rem;
    font-weight: bold;
    color: #2c3e50;
    font-variant-numeric: tabular-nums;
    margin: 20px 0;
    transition: color 0.6s ease, transform 0.3s ease;
}

.session-counter {
    background: #f8f9fa;
