// Timer state
let isRunning = false;
let isWorkMode = true;
let timeRemaining = 25 * 60; // 25 minutes in seconds
let timerInterval = null;
let sessionsCompleted = 0;

// Constants
const WORK_TIME = 25 * 60; // 25 minutes
const BREAK_TIME = 5 * 60; // 5 minutes

// DOM elements
const countdownEl = document.getElementById('countdown');
const stateLabelEl = document.getElementById('stateLabel');
const sessionCountEl = document.getElementById('sessionCount');
const startBtn = document.getElementById('startBtn');
const pauseBtn = document.getElementById('pauseBtn');
const resetBtn = document.getElementById('resetBtn');
const notificationEl = document.getElementById('notification');
const containerEl = document.querySelector('.container');

// Format time as MM:SS
function formatTime(seconds) {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
}

// Update display
function updateDisplay() {
    countdownEl.textContent = formatTime(timeRemaining);
    stateLabelEl.textContent = isWorkMode ? 'Work' : 'Break';
    stateLabelEl.className = `state-label ${isWorkMode ? 'work' : 'break'}`;

    // Update container mode class
    containerEl.className = `container ${isWorkMode ? 'work-mode' : 'break-mode'}`;

    // Add alert animation when less than 10 seconds
    if (timeRemaining <= 10 && timeRemaining > 0) {
        countdownEl.classList.add('alert');
    } else {
        countdownEl.classList.remove('alert');
    }
}

// Play notification sound (using Web Audio API)
function playNotificationSound() {
    const audioContext = new (window.AudioContext || window.webkitAudioContext)();
    const oscillator = audioContext.createOscillator();
    const gainNode = audioContext.createGain();

    oscillator.connect(gainNode);
    gainNode.connect(audioContext.destination);

    oscillator.frequency.value = 800;
    oscillator.type = 'sine';

    gainNode.gain.setValueAtTime(0.3, audioContext.currentTime);
    gainNode.gain.exponentialRampToValueAtTime(0.01, audioContext.currentTime + 0.5);

    oscillator.start(audioContext.currentTime);
    oscillator.stop(audioContext.currentTime + 0.5);
}

// Show notification
function showNotification(message, type) {
    notificationEl.textContent = message;
    notificationEl.className = `notification show ${type}`;

    setTimeout(() => {
        notificationEl.classList.remove('show');
    }, 3000);
}

// Timer complete
function onTimerComplete() {
    playNotificationSound();

    if (isWorkMode) {
        // Work session completed
        sessionsCompleted++;
        sessionCountEl.textContent = sessionsCompleted;

        // Animate session counter
        sessionCountEl.style.transform = 'scale(1.3)';
        setTimeout(() => {
            sessionCountEl.style.transform = 'scale(1)';
        }, 300);

        showNotification('Work session complete! Time for a break.', 'work-complete');

        // Switch to break mode
        isWorkMode = false;
        timeRemaining = BREAK_TIME;
    } else {
        // Break completed
        showNotification('Break complete! Ready for another work session?', 'break-complete');

        // Switch to work mode
        isWorkMode = true;
        timeRemaining = WORK_TIME;
    }

    // Animate state transition
    stateLabelEl.style.transform = 'scale(1.1)';
    setTimeout(() => {
        updateDisplay();
        setTimeout(() => {
            stateLabelEl.style.transform = 'scale(1)';
        }, 100);
    }, 200);

    // Auto-start next session
    startTimer();
}

// Start timer
function startTimer() {
    if (isRunning) return;

    isRunning = true;
    startBtn.disabled = true;
    pauseBtn.disabled = false;

    timerInterval = setInterval(() => {
        timeRemaining--;
        updateDisplay();

        if (timeRemaining <= 0) {
            stopTimer();
            onTimerComplete();
        }
    }, 1000);
}

// Pause timer
function pauseTimer() {
    stopTimer();
    startBtn.disabled = false;
    pauseBtn.disabled = true;
}

// Stop timer (internal function)
function stopTimer() {
    isRunning = false;
    clearInterval(timerInterval);
    timerInterval = null;
}

// Reset timer
function resetTimer() {
    stopTimer();

    // Reset to work mode with full time
    timeRemaining = isWorkMode ? WORK_TIME : BREAK_TIME;

    updateDisplay();
    startBtn.disabled = false;
    pauseBtn.disabled = true;
    countdownEl.classList.remove('alert');
}

// Event listeners
startBtn.addEventListener('click', startTimer);
pauseBtn.addEventListener('click', pauseTimer);
resetBtn.addEventListener('click', resetTimer);

// Initialize display
updateDisplay();

